#include <stdio.h>
#include <stdlib.h>

//Power digit sum:
//2^15 = 32768; the sum of its digits is 3+2+7+6+8=26
//This program calculates the sum of the digits of 2^1000. This number is
//1.0715e+301 -- 301 digits long! 
/* Follows is the full number 2^1000 represents in all of its glory:
 * 
 */
//for now, I do not want to try an implement arbitrary numbers in C.
int main(){
int sum=0,i=0;
const char p[]="";

while(p[i] != '\0'){
  int* temp=atoi(p[i]);
  sum+=*temp;
  //printf("%i",atoi(p[i]));
  i++;
}
printf("\ngrand total: %i\n",sum);
return 0;
}
